mapper.insert:
INSERT INTO salsa_comment(
        comment_idx
        , comment_sn
        , writer_user_idx
        , regdate
        , content
        , use_yn
        )
VALUES (
        #{comment_idx}
        , (SELECT IFNULL( max( comment_sn ) + 1, 1 ) FROM salsa_comment as comment where comment_idx = #{comment_idx})
        , #{writer_user_idx}
        , now()
        , #{content}
        , 'Y'
        )
